def longest_palindrome(s)
  length = s.length
  h = {}
  str_max = ''
  length.times do |i|
    if h[s[i]]
      h[s[i]].unshift(i)
    else
      h[s[i]] = [i]
    end
  end

  length.times do |i|
    h[s[i]].each do |j|
      if s[i..j] == s[i..j].reverse
        str_max = s[i..j] if str_max.length < j - i + 1
      end
    end
  end
  str_max
end

puts longest_palindrome("")
