# def find_length(a, b)
#   size = a.size
#   ret = []
#   size.times {ret.push([0] * size)}
#   max = 0
#   (0..size-1).each do |j|
#     (0..size-1).each do |i|
#       if a[j] == b[i]
#         if i == 0 || j == 0
#           ret[j][i] = 1
#         else
#           ret[j][i] = ret[j - 1][i - 1] + 1
#         end
#         max = [max,ret[j][i]].max
#       else
#         ret[j][i] = 0
#       end
#     end
#   end
#   max
# end
def find_length(a, b)
  size = a.size
  ret = []
  size.times {ret.push([0] * size)}
  max = 0
  (0..size-1).each do |j|
    return max if max >= size - j
    (0..size-1-j).each do |i|
      if a[j+i] == b[i]
        if i == 0
          ret[j+i][i] = 1
        else
          ret[j+i][i] = ret[j+i - 1][i - 1] + 1
        end
        max = [max,ret[j+i][i]].max
      else
        ret[j+i][i] = 0
      end
      if j > 0
        if a[i] == b[j+i]
          if i == 0
            ret[i][j+i] = 1
          else
            ret[i][j+i] = ret[i-1][j+i-1] + 1
          end
          max = [max,ret[i][j+i]].max
        else
          ret[i][j+i] = 0
        end
      end
    end
  end
  max
end
